import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search } from 'lucide-react';

const Education = () => {
  const [resources, setResources] = useState([]);
  const [filteredResources, setFilteredResources] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const fetchResources = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/education');
        setResources(response.data);
        setFilteredResources(response.data);
      } catch (error) {
        console.error('Error fetching education resources:', error);
      }
    };

    fetchResources();
  }, []);

  useEffect(() => {
    const results = resources.filter(resource =>
      resource.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (selectedCategory === 'All' || resource.category === selectedCategory)
    );
    setFilteredResources(results);
  }, [searchTerm, selectedCategory, resources]);

  const categories = ['All', ...new Set(resources.map(resource => resource.category))];

  return (
    <div>
      <h2 className="text-3xl font-bold mb-8">Financial Education Hub</h2>
      <div className="mb-6 flex items-center">
        <div className="relative flex-grow">
          <input
            type="text"
            placeholder="Search resources..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full p-2 pl-10 border rounded-l"
          />
          <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
        </div>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          className="p-2 border rounded-r"
        >
          {categories.map(category => (
            <option key={category} value={category}>{category}</option>
          ))}
        </select>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredResources.map((resource) => (
          <div key={resource._id} className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow">
            <h3 className="text-xl font-semibold mb-2">{resource.title}</h3>
            <p className="mb-4">{resource.content}</p>
            <div className="flex justify-between text-sm text-gray-500">
              <span>Language: {resource.language}</span>
              <span>Category: {resource.category}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Education;
