import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Marketplace = () => {
  const [grants, setGrants] = useState([]);

  useEffect(() => {
    const fetchGrants = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/microgrants');
        setGrants(response.data);
      } catch (error) {
        console.error('Error fetching microgrants:', error);
      }
    };

    fetchGrants();
  }, []);

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Microgrant Marketplace</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {grants.map((grant) => (
          <div key={grant._id} className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-xl font-semibold mb-2">{grant.businessName}</h3>
            <p className="mb-2">{grant.description}</p>
            <p className="mb-2">Amount Requested: ${grant.amountRequested}</p>
            <p className="mb-2">Status: {grant.status}</p>
            <p className="text-sm text-gray-500">Owner: {grant.owner.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Marketplace;
