import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Education = () => {
  const [resources, setResources] = useState([]);

  useEffect(() => {
    const fetchResources = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/education');
        setResources(response.data);
      } catch (error) {
        console.error('Error fetching education resources:', error);
      }
    };

    fetchResources();
  }, []);

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Financial Education Hub</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {resources.map((resource) => (
          <div key={resource._id} className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-xl font-semibold mb-2">{resource.title}</h3>
            <p className="mb-2">{resource.content}</p>
            <p className="text-sm text-gray-500">Language: {resource.language}</p>
            <p className="text-sm text-gray-500">Category: {resource.category}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Education;
